	public static Utente validaDipendente(String nome,String cognome,String email) {
		System.out.println("inizio metodo Utlity "+email+" "+nome+" "+cognome);
		Utente dipendente=null;
		Pattern ptr = Pattern.compile("^\\p{L}+[\\p{L}\\p{Z}\\p{P}]{0,}");
		boolean nomeValidate = (ptr.matcher(nome).matches() ? true : false);
		boolean cognomeValidate = (ptr.matcher(cognome).matches() ? true : false);
		boolean emailValidate = validaEmail(email);
		if (nomeValidate && cognomeValidate && emailValidate) {
			System.out.println("nell if di valida dipendente");
		dipendente= new Utente(nome,cognome,email,generaPassword(),1,"dip");
		System.out.println("nel metodo validaDipendente  "+dipendente);
		}
		return dipendente;
		
	}
	public static Utente validaStudente(String email,String nome,String cognome,String password) {
		Utente studente=null;
		Pattern ptr = Pattern.compile("^\\p{L}+[\\p{L}\\p{Z}\\p{P}]{0,}");
		boolean nomeValidate = (ptr.matcher(nome).matches() ? true : false);
		boolean cognomeValidate = (ptr.matcher(cognome).matches() ? true : false);
		boolean emailValidate = validaEmail(email);
		if (nomeValidate && cognomeValidate && emailValidate) {
		studente= new Utente(nome,cognome,email,password,0,"stud");
			
		}
		return studente;
		
	}
	
	public static String generaPassword() {				                                                      
		String password="";
		String[] caratteri= {"DG=1","G2#w","D^f3","4=fD","5*Df","6S@e","7fK","8m2","Ft+7","f4d","S$","Z","Rft","ùéE","F#e4","fH@1","L3^R","KuY","As4","Dm7","xc5","kK3","P0o","rR2","5Rs"};
		for (int i = 0; i <=2; i++) {
			int idx = new Random().nextInt(caratteri.length);
			String value = (caratteri[idx]);
			password+=value;
		}
	
		return password;
	}
	public static void inviaEmail(Utente dipendente) {
		System.out.println(dipendente);
        String emailDip = "c.davide18.93@gmail.com";
        String emailAdmin = "mario.rossijava@libero.it";
        String host = "localhost";

        Properties properties = System.getProperties();
        properties.setProperty("smtp.libero.it", host);

        Session session = Session.getDefaultInstance(properties);

        try {
            MimeMessage message = new MimeMessage(session);
            message.setFrom(new InternetAddress(emailAdmin));
            message.addRecipient(Message.RecipientType.TO, new InternetAddress(emailDip));
         //   message.addRecipients(Message.RecipientType.TO, new Address[]); // email to multiple recipients
            message.setSubject("Benvenuto"+" "+dipendente.getNome()+" "+dipendente.getCognome());
            message.setText("Sei stato registrato correttamente ecco le tue credenziali d'accesso: "
            		+"email "+dipendente.getEmail()+"  password "+dipendente.getPassword());

            Transport.send(message);

        } catch (AddressException e) {
            e.printStackTrace();
        } catch (MessagingException e) {
            e.printStackTrace();
        }
    }


